                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '3773', 'Maya', '', 'No biography written.', 
                    'Kamaya_Tsumonsieur', 'characters/Kamaya_Tsumonsieur/primary.jpg', '[]',
                    NULL, NULL, '', '', 
                    '', '', '', 'Active',
                    '', '', '', '',
