                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '9327', 'Lafitte', '', 'Lafitte is the Blackbeard pirate crew''s navigator He used to be a policeman in West Blue, but he became a pirate after being exiled from the police force and banned from the West Blue for being excessively violent and cruel. He is incredibly pale and sickly looking, yet is very stealthy and tap-dances while he walks. (Source: One Piece Wikia)', 
                    'Lafitte', 'characters/Lafitte/primary.jpg', '[]',
                    NULL, NULL, '', '', 
                    '', '', '', 'Active',
