                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '20292', 'Bege Capone', '', 'Capone "Gang" Bege is the captain of the Firetank Pirates, and hails from West Blue. His bounty is 138 million beli, and he is one of the 11 Supernovas.', 
                    NULL, NULL, '[]',
