INSERT INTO op_characters (
                    character_id_mal, name, name_kanji, about, image_folder, image_url, gallery_images,
                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '23025', 'McKinley', '', 'McKinley (マッキンリー) is the Captain of the White Berets (a police force in Skypeia), who tried to arrest the Straw Hat Pirates when they first arrived in Skypeia. He appears to be very devoted to Eneru, until it is revealed that Eneru planned to destroy Skypeia with his Devil Fruit powers. McKinley then reveals that he only did everything Eneru commanded in order to ensure the safety of the citizens of Sky Island. McKinley, along with the rest of the White Berets, then leave Eneru''s side and choose to help Conis evacuate the Skypeian people. First Appearance: Chapter 241; Episode 155 (Source: One Piece Wikia)', 
                    'McKinley', 'characters/McKinley/primary.jpg', '[]',
                    NULL, NULL, '', '', 
                    '', '', '', 'Active',
