INSERT INTO op_characters (
                    character_id_mal, name, name_kanji, about, image_folder, image_url, gallery_images,
                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '5421', 'Brogy', '', 'Brogy the Red Ogre (赤鬼のブロギー, Akaoni no Burogī) is one of the two legendary giants who fight an endless battle on Little Garden, an island in the Grand Line. Each giant has a bounty of 100,000,000 on their heads. (Source: One Piece Wiki)', 
                    'Brogy', 'characters/Brogy/primary.jpg', '[]',
                    NULL, NULL, '', '', 
                    '', '', '', 'Active',
