INSERT INTO op_characters (
                    character_id_mal, name, name_kanji, about, image_folder, image_url, gallery_images,
                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '13018', 'Sengoku', '', 'The supreme commander of all Naval forces in the One Piece world, his status apparent by the life-size seagull attached to his hat. He oversees the workings of the Navy from his base in the Sanctuary of Marie Joie, and is subordinate only to the leaders of the World Government, the Gorōsei. He also has a pet goat. "Sengoku" can be written in kanji  mean "warring states," which is a period of Japanese history. He was the one who, twenty years ago as an admiral, entrusted the Golden Den Den Mushi to Spandine, allowing him to order the Buster Call on Ohara. He also seems to dislike the Shichibukai like Vice Admiral Smoker. Sengoku is considered by Whitebeard as one of the few people that know the seas from the time of Gold Roger. He resigned from his position after the war with Whitebeard.', 
                    'Sengoku', 'characters/Sengoku/primary.jpg', '[]',
                    (SELECT id FROM op_devil_fruits WHERE name = 'Hito Hito no Mi'), NULL, '', '', 
                    '', '', '', 'Active',
