INSERT INTO op_characters (
                    character_id_mal, name, name_kanji, about, image_folder, image_url, gallery_images,
                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '20091', 'Hina', '', 'Captain Hina (ヒナ大佐 Hina Taisa) is an officer in the Marines, and the object of both Jango''s and Fullbody''s affections.  She has received the nickname "Hina the Black Cage" because of her Ori Ori no Mi ability, which allows her to form restraints on people by swiping her limbs through them.  Hina has a tendency to speak in the third person, though this only seems to add to her attractiveness for her subordinates. She is a long-time friend of Commodore Smoker, joining the Marines at the same time as him, and helping him get out of situations where Smoker could have been fired. (Source: One Piece Wiki)', 
                    'Banchina', 'characters/Banchina/primary.jpg', '[]',
                    NULL, NULL, '', '', 
                    '', '', '', 'Active',
