INSERT INTO op_characters (
                    character_id_mal, name, name_kanji, about, image_folder, image_url, gallery_images,
                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '20293', 'Basil Hawkins', '', 'Basil Hawkins is an infamous pirate known as the "Magician" and the captain of the Hawkins Pirates. He comes from North Blue and is one of eleven pirates who have been referred to as "The Eleven Supernovas" on Sabaody Archipelago whose bounties are higher than 100,000,000 beri when the Straw Hat Pirates arrived. His bounty amounts to 249,000,000 beri, which was the third highest known on the island at the time, after Eustass Kid and Monkey D. Luffy.', 
                    'Bas', 'characters/Bas/primary.jpg', '[]',
