INSERT INTO op_characters (
                    character_id_mal, name, name_kanji, about, image_folder, image_url, gallery_images,
                    devil_fruit_id, faction_id, position, epithet, origin, residence, occupation, status,
                    birthday, blood_type, height, age, bounty, bounty_formatted
                ) VALUES (
                    '20294', 'Urouge', '', 'Urouge is a monk turned pirate who hails from a sky island like Skypiea. He is one of eleven pirates who have been referred to as "The Eleven Supernovas" on Sabaody Archipelago whose bounties are higher than  100,000,000 when the Straw Hats arrive in the place. He is the one with the lowest bounty of the said eleven. Urouge is from a Sky Island, judging by the style of the wings on his back this would indicate he is of Bilkan origins', 
                    'Urouge', 'characters/Urouge/primary.jpg', '["characters/Urouge/urouge_1.jpg", "characters/Urouge/urouge_2.jpg"]',
                    NULL, NULL, '', '', 
                    '', '', '', 'Active',
                    '', '', '', '',
